import React, { useState, useEffect } from 'react';
import { useAppContext } from "../libs/contextLib";
import "./DownloadImages.css";
import { Image } from 'react-bootstrap';
import { onError } from '../libs/errorLib';
import { API, Storage } from 'aws-amplify';


export default function DownloadImages() {
    const [images, setImages] = useState([]);
    const {isAuthenticated} = useAppContext();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function onLoad() {
            if(!isAuthenticated) {
                return;
            }

            try {
                const images = await loadImageInfo();

                const runAsync = async () => {
                    await asyncForEach(images, async (item) => {
                        item.src = await Storage.get(item.imageName, { level: 'public' });
                    });
                }

                runAsync();
                setImages(images);
            } catch (e) {
                onError(e);
            }

            console.log("Finished loading effects")
            setIsLoading(false);
        }


        onLoad();
    }, [isAuthenticated]);

    async function loadImageInfo() {
        const imageInfo = await API.get("notes", "/images");


        console.log("Loaded all img info")
        return imageInfo;

    }

    async function asyncForEach(array, callback) {
        for (let index = 0; index < array.length; index++) {
            await callback(array[index], index, array);
        }
    }

    function ezLoad() {
        console.log("Attempted load");
        const buffer = images.map((image, i) => {
            return (
                <div id={i} key={i}>
                    <Image key={i} src={image.src} />
                </div>
            );
        });

        console.log(buffer);
        return (
            <div id='buffer'>
                {buffer}
            </div>
        );
    }
