import React, { useState, useEffect, Suspense } from 'react';
import { LinkContainer } from "react-router-bootstrap";
import { PageHeader, ListGroup, Image, Row, Col } from "react-bootstrap";
import { useImage } from "react-image";
import { useAppContext } from "../libs/contextLib";
import "./DownloadImages.css";
import { onError } from '../libs/errorLib';
import { API, Storage } from 'aws-amplify';
import ImageDisplay from './ImageDisplay';


export default function DownloadImages() {
    const [images, setImages] = useState([]);
    const {isAuthenticated} = useAppContext();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function onLoad() {
            if(!isAuthenticated) {
                return;
            }

            try {
                const images = await loadImages();
                setImages(images);
            } catch (e) {
                onError(e);
            }

            setIsLoading(false);
        }

        onLoad();
    }, [isAuthenticated]);

    async function loadImages() {
        const images = await API.get("notes", "/images")
        images.forEach(async item => {
            item.imageFile = await Storage.get(item.imageName, { level: 'public' });
        });
        console.log(images);

        return images;
    }

    /*
    return (
        <ImageDisplay></ImageDisplay>
    );
    */

}
