import React, { useState, useEffect, Suspense } from 'react';
import { LinkContainer } from "react-router-bootstrap";
import { PageHeader, ListGroup, Image, Row, Col } from "react-bootstrap";
import { useImage } from "react-image";
import { useAppContext } from "../libs/contextLib";
import "./DownloadImages.css";
import { onError } from '../libs/errorLib';
import { API, Storage } from 'aws-amplify';
import ImageDisplay from './ImageDisplay';


export default function DownloadImages() {
    const [images, setImages] = useState([]);
    const {isAuthenticated} = useAppContext();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function onLoad() {
            if(!isAuthenticated) {
                return;
            }

            try {
                const images = await loadImages();
                setImages(images);
            } catch (e) {
                onError(e);
            }

            setIsLoading(false);
        }

        onLoad();
    }, [isAuthenticated]);

    async function loadImages() {
        const images = await API.get("notes", "/images")
        images.forEach(async item => {
            item.imageFile = await Storage.get(item.imageName, { level: 'protected' });
        });
        console.log(images);

        return images;
    }

    function renderPhotosList(images) {
        const idealCols = 5;
        const rows = Math.ceil(images.length/idealCols);

        const imagesOut = [].concat(images.map((image, i) => 
            <ImageComponent image />
        ));

        const fullRows = []
        for (var i = 0; i < rows; i++) {
            fullRows.push(
                <Row>
                    <Col>{imagesOut.pop}</Col>
                </Row>
            );
        }

        return fullRows;
    }

    function ImageComponent(image) {
        const {src} = useImage({
          srcList: image.imageFile,
        });

        return <Image src={src}  />
      }

    function renderPhotos() {
        return (
            <div className='photos'>
                <PageHeader>Images</PageHeader>
                <ListGroup>
                    {!isLoading && renderPhotosList(images)}
                </ListGroup>

            </div>
        )
    }


    return (
        <ImageDisplay></ImageDisplay>
    );
}
