import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Tabs, Tab } from 'react-bootstrap';
import { API, Storage } from "aws-amplify";
import { onError } from "../libs/errorLib";
import './Images.css';


export default function Images() {
    const {id} = useParams();
    const [image, setImage] = useState(null);
    const [title, setTitle] = useState("");
    //const [isLoading, setIsLoading] = useState(false);

    console.log("id: " + id)
    useEffect(() => {
        console.log('useEffect flag')

        function loadImage() {
            return API.get("notes", `/images/${id}`);
        }

        async function onLoad() {
            try {
                const image = await loadImage();
                const { title, imgName } = image;

                console.log("IMAGE:");
                console.log(image);


                image.attatchmentURL = await Storage.get(imgName, { level: 'protected' });

                console.log("AFTER GET:");
                console.log(image);

                //setIsLoading(false);
                setTitle(title);
                setImage(image);
            } catch (e) {
                onError(e);
            }
        }

        onLoad();
    }, [id]);


    console.log("image: " + image);
    if (image != null){
        console.log(image.attatchmentURL);
    }

    function Image(props) {
        const isLoading = props.isLoading;
        if (!isLoading) {
            return (
                <div className='Images'>
                    <h1>{title}</h1>
                    <Tabs defaultActiveKey='Image' id='infoTabs'>
                        <Tab eventKey='Image' title='Image'>
                            <Image src={image ? image.attatchmentURL : '#'}></Image>
                        </Tab>

                        <Tab eventKey='tags' title='Tags'>
                            {image ? image.tags : "no Tags"}
                        </Tab>

                        <Tab eventKey='metadata' title='Metadata'>
                            <h2>Map of metadata</h2>
                        </Tab>
                    </Tabs>
                </div>
            );
        } else {
            return (
                <h3>Loading</h3>
            );
        }

    }

    return <Image isLoading={false} />;    

}
