import React from 'react';
import { Image } from 'react-bootstrap';


export default function ImageDisplay(props) {
    /*props.images should be formatted as follows
    [
        {
            src: imgName, #for loading the image from S3
            imgID: id, #For loading the image data/redirection
            tags: [
                tag1,
                tag2
                ]

        },
    ]
    */

   const { images } = props;
   const toRender = images.length;
   const rows = Math.ceil(toRender/5);


   const imgList = [];

   console.log("Att render");
   console.log(toRender);
   images.forEach(element => {
       console.log(element);
   });
   console.log(rows);

   for (var i = 0; i < rows; i++) {
       imgList[i] = [];
   }

   for (var arrayLoop = 0; arrayLoop < rows; arrayLoop++) {
       for (var innerLoop = 0; innerLoop < 5; innerLoop++) {
            imgList[arrayLoop].push(images.pop());
       }
   }

   //5 images (full)
   function renderRow(images) {
        console.log("att 5");

        return (
            <row>
                <col><Image src={images[0].src} /></col>
                <col><Image src={images[1].src} /></col>
                <col><Image src={images[2].src} /></col>
                <col><Image src={images[3].src} /></col>
                <col><Image src={images[4].src} /></col>
            </row>
       );
    }
   //4
    function renderRowFour(images) {
        console.log("att 4");

        return (
            <row>
                <col><Image src={images[0].src} /></col>
                <col><Image src={images[1].src} /></col>
                <col><Image src={images[2].src} /></col>
                <col><Image src={images[3].src} /></col>
                <col></col>
            </row>
        );
    }
    //3
    function renderRowThree(images) {
        console.log("att 3");

        return (
            <row>
                <col><Image src={images[0].src} /></col>
                <col><Image src={images[1].src} /></col>
                <col><Image src={images[2].src} /></col>
                <col></col>
                <col></col>
            </row>
        );
    }
    //2
    function renderRowTwo(images) {
        console.log("att 2");

        return (
            <row>
                <col><Image src={images[0].src} /></col>
                <col><Image src={images[1].src} /></col>
                <col></col>
                <col></col>
                <col></col>
            </row>
        );
    }
    //1
    function renderRowOne(images) {
        console.log("att 1");

        return (
            <row>
                <col><Image src={images[0].src} /></col>
                <col></col>
                <col></col>
                <col></col>
                <col></col>
            </row>
        );
    }

    function renderLast() {
        switch(images.length) {
            case 4:
                renderRowFour();
                break;
            case 3:
                renderRowThree();
                break;
            case 2:
                renderRowTwo();
                break;
            case 1: 
                renderRowOne();
                break;
            default:
                return null;

        }
    }

    function renderFull() {
       for (var i = 0; i < rows; i++){
           console.log("Rendering a full row!")
           renderRow(imgList[i]);
       }
       renderLast()
       console.log("Done rendering")
    }




    return renderFull();





}
