import React, {useRef, useState, createContext} from 'react';
import {useHistory} from "react-router-dom";
import {FormGroup, FormControl, ControlLabel} from "react-bootstrap";
import { API } from 'aws-amplify';
import LoaderButton from "../components/LoaderButton";
import {onError} from "../libs/errorLib"
import config from "../config"
import "./NewNote.css"
import { s3Upload } from '../libs/awsLib';


export default function NewNote() {
    const file = useRef(null);
    const history = useHistory();
    const [content, setContent] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    function validateForm() {
        return content.length > 0;
    }

    function handleFileChange(event) {
        file.current = event.target.files[0];
    }

    async function handleSubmit(event) {
        event.preventDefault();

        if (file.current && file.current.size > config.MAX_ATTATCHMENT_SIZE) {
            alert(
                `Please pick a file smaller than ${config.MAX_ATTATCHMENT_SIZE /
                     1000000} MB.`
            );
            return;
        }
        setIsLoading(true);

        try {
            const attatchment = file.current ? await s3Upload(file.current) : null;

            await createNote({ content, attatchment });
            history.push('/');
        } catch (e) {
            onError(e);
            setIsLoading(false);
        }
    }

    function createNote(note) {
        return API.post("notes", "/notes", {
            body: note
        });
    }

    return (
        <div className="NewNote">
            <form onSubmit={handleSubmit}>
                <FormGroup controlId="content">
                    <FormControl
                        value={content}
                        componentClass="textarea"
                        onChange={e => setContent(e.target.value)}
                    />
                </FormGroup>
                <FormGroup controlId='file'>
                    <ControlLabel>Attatchment</ControlLabel>
                    <FormControl
                        onChange={handleFileChange}
                        type="file"
                    />
                </FormGroup>
                <LoaderButton
                    block
                    type="submit"
                    bsSize='large'
                    bsStyle='primary'
                    isLoading={isLoading}
                    disabled={!validateForm()}
                >
                    Create
                </LoaderButton>
            </form>
        </div>
    );
}
