import React, { useState } from 'react';
import { Image, Button, Modal, ModalHeader, } from 'react-bootstrap';
import { API } from 'aws-amplify';
import "./CommentTab.css";
import { LinkContainer } from 'react-router-bootstrap';

function renderImage(attatchmentURL) {
    return (
        <div id="container">
            <Image id='commentImage' src={attatchmentURL}></Image>
            <div id='commentBox'>

            </div>
        </div>
    );
}

function renderCommentsList(comments) {
    return [{}].concat(comments).map((comment, i) =>
        i !== 0 ? (
            <h3>list placeholder</h3>
        ) : (
            <h3> add comment </h3>
        )
    );
}


export default function CommentTab(props) {
    const { image } = props;
    const { attatchmentURL, comments } = image;
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);




     return (
        <>
            <div id='left'>
                { renderImage(attatchmentURL) }
            </div>
            <div id='right'>
                { renderCommentsList(comments) }
                <>
                    <Button variant='primary' onClick={handleShow}>
                        <b>{"\uFF0B"}</b> Add a comment
                    </Button>


                </>
            </div>

            <Modal 
                show={show} 
                onHide={handleClose}
                backdrop="static"
                keyboard={false}
            >
                <Modal.header closeButton>
                    <Modal.Tital>Add a comment</Modal.Tital>
                </Modal.header>
                <Modal.body>
                    Add comment tech
                </Modal.body>
                <Modal.Footer>
                    <Button variant='secondary' onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary">Submit</Button>
                </Modal.Footer>
            </Modal>
        </>
     )   
}
