import React, { useRef, useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import { Tabs, Tab } from 'react-bootstrap';
import { API, Storage } from "aws-amplify";
import LoaderButton from "../components/LoaderButton";
import { s3Upload } from "../libs/awsLib";
import { onError } from "../libs/errorLib";
import config from "../config";
import './Notes.css';


export default function Notes() {
    const file = useRef(null);
    const {id} = useParams();
    const history = useHistory();
    const [note, setNote] = useState(null);
    const [content, setTitle] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [isDeleting, setIsDeleting] = useState(false);

    useEffect(() => {
        function loadImage() {
            return API.get("notes", `/images/${id}`);
        }

        async function onLoad() {
            try {
                const image = await loadImage();
                const {title, imgName} = image;

                if (imgName) {
                    image.attatchmentURL = await Storage.vault.get(imgName);
                }

                setTitle(title);
                setNote(image);
            } catch (e) {
                onError(e);
            }
        }

        onLoad();
    }, [id]);

    function formatFilename(str) {
        return str.replace(/^\w+-/, "");
    }

    function handleFileChange(event) {
        file.current = event.target.files[0];
    }

    function saveNote(note) {
        return API.put("notes", `/notes/${id}`, {
          body: note
        });
      }

      async function handleSubmit(event) {
        let attachment;

        event.preventDefault();

        if (file.current && file.current.size > config.MAX_ATTACHMENT_SIZE) {
          alert(
            `Please pick a file smaller than ${
              config.MAX_ATTACHMENT_SIZE / 1000000
            } MB.`
          );
          return;
        }

        setIsLoading(true);

        try {
          if (file.current) {
            attachment = await s3Upload(file.current);
          }

          await saveNote({
            content,
            attachment: attachment || note.attachment
          });
          history.push("/");
        } catch (e) {
          onError(e);
          setIsLoading(false);
        }
      }

    function deleteNote() {
        return API.del("notes", `/notes/${id}`);
    }

    async function handleDelete(event) {
        event.preventDefault();

        const confirmed = window.confirm(
            "Are you sure you want to delete this note?"
        );

        if (!confirmed) {
            return;
        }

        setIsDeleting(true);

        try {
            await deleteNote();
            history.push("/");
        } catch (e) {
            onError(e);
            setIsDeleting(false);
        }
    }

    return (
        <div className='ImgSection'>
            <Tabs defaultActiveKey='Image' id='infoTabs'>
                <Tab eventKey='Image' title='Image'>
                    <Image src='image.attatchmentURL'></Image>
                </Tab>
            </Tabs>
        </div>
    );
}
