import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Tabs, Tab, Image } from 'react-bootstrap';
import { API, Storage } from "aws-amplify";
import { onError } from "../libs/errorLib";
import './Notes.css';


export default function Notes() {
    const {id} = useParams();
    const [image, setImage] = useState(null);
    const [title, setTitle] = useState("");

    useEffect(() => {
        function loadImage() {
            return API.get("notes", `/images/${id}`);
        }

        async function onLoad() {
            try {
                const image = await loadImage();
                const {title, imgName} = image;

                if (imgName) {
                    image.attatchmentURL = await Storage.get(imgName);
                }

                setTitle(title);
                setImage(image);
            } catch (e) {
                onError(e);
            }
        }

        onLoad();
    }, [id]);

    console.log(image);
    if (image != null){
        console.log(image.attatchmentURL);
    }
    return (
        <div className='ImgSection'>
            <Tabs defaultActiveKey='Image' id='infoTabs'>
                <Tab eventKey='Image' title={title}>
                    <Image src='image.attatchmentURL'></Image>
                </Tab>
            </Tabs>
        </div>
    );
}
