import React, { useState, useEffect } from 'react';
import { useAppContext } from "../libs/contextLib";
import "./DownloadImages.css";
import { onError } from '../libs/errorLib';
import { API, Storage } from 'aws-amplify';
import ImageDisplay from './ImageDisplay';


export default function DownloadImages() {
    const [images, setImages] = useState([]);
    const {isAuthenticated} = useAppContext();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function onLoad() {
            if(!isAuthenticated) {
                return;
            }

            try {
                const images = await loadImages();
                setImages(images);
            } catch (e) {
                onError(e);
            }

            console.log("Finished loading")
            setIsLoading(false);
        }

        onLoad();
    }, [isAuthenticated]);

    async function loadImages() {
        const imageList = await API.get("notes", "/images")
        imageList.forEach(async item => {
            console.log("Getting image", item.imageName);
            item.src = await Storage.get(item.imageName, { level: 'public' });
            console.log("Item Gotten", item.imageName);
        });
        console.log(imageList);

        return imageList;
    }

    function renderList() {
        console.log("Rendering!");
        console.log(images[0].src);
        return <ImageDisplay images={images} />
    }


    return (
        <div>
            {isLoading ? (
                <div>Loading...</div>
            ) : (
                renderList()
            )}
        </div>

    );


}
