import React, { useState, useEffect } from 'react';
import { useAppContext } from "../libs/contextLib";
import "./DownloadImages.css";
import { onError } from '../libs/errorLib';
import { API, Storage } from 'aws-amplify';
import ImageDisplay from './ImageDisplay';


export default function DownloadImages() {
    const [images, setImages] = useState([]);
    const {isAuthenticated} = useAppContext();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        async function onLoad() {
            if(!isAuthenticated) {
                return;
            }

            try {
                const images = await loadImages();
                setImages(images);
            } catch (e) {
                onError(e);
            }

            setIsLoading(false);
        }

        onLoad();
    }, [isAuthenticated]);

    async function loadImages() {
        const images = await API.get("notes", "/images")
        images.forEach(async item => {
            item.src = await Storage.get(item.imageName, { level: 'protected' });
        });
        console.log(images);
        return images;
    }

    function renderList() {
        return <ImageDisplay images={images} />
    }

    console.log(images.length);
    console.log(images);
    if (images.length === 0 && !isLoading) {
        return <h2>No Images to display</h2>
    } else {
        return renderList()
    }



}
